import React, { useState } from 'react';
import './App.css';
const api = {
  key: "a0df74752ee6fa22975dd7164bb0725a",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }



  return (
    <div>
      <main>
      <div className="search-box">
      <input
      type ="text"
      className="search-bar"
      placeholder="Search..."
      onChange={e => setQuery(e.target.value)}
      value={query}
      onKeyPress={search}
      />
      </div>
      <h1 className='text-center'>Search above by City Name</h1>
