import React, { useState } from 'react';
import './App.css';
const api = {
  key: "a0df74752ee6fa22975dd7164bb0725a",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${city}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }


  let date = new Date();
  return (
    <div>
      <div className="card-img-overlay">
        <form>
          <div className="input-group mb-4 w-75 mx-auto">
            <input
              type="text"
              className="search-bar"
              placeholder="Search..."
              onChange={(e) => setCity(e.target.value)}
              value={city}
              onKeyPress={search}
            />
          </div>
        </form>
        {(typeof weather.main != "undefined") ? (
          <div className="bg-dark bg-opacity-50 py-3">
            <h2 className="card-title">
              {weather.name},{weather.sys.country}
            </h2>
            <p className="card-text lead">{date.toDateString()}</p>
            <hr></hr>
            <i className="fas fa-cloud fa-4x"></i>
            <h1 className="fw-bolder mb-5">
              {Math.round(weather.main.temp)}&deg;C
            </h1>
            <p className="fw-bolder mb-0">cloud</p>
            <p className="lead"> 30.0 &deg;C | 33.01&deg;C</p>
          </div>
        ) : ('')}
      </div>
    </div>
  );
}

export default App;
