    };
    fetchweather();
  }, []);

  let date = new Date();
  return (
    <div>
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-4">
            <div class="card text-white text-center border-0">
              <img
                src="https://source.unsplash.com/600x900/?nature,water"
                className="card-img"
                alt="..."
              />
              <div class="card-img-overlay">
                <form>
                  <div className="input-group mb-4 w-75 mx-auto">
                    <input
                      type="search"
                      className="form-control"
                      placeholder="Location..."
                      aria-label="Location..."
                      aria-describedby="basic-addon2"
                    />
                    <button
                      type="submit"
                      className="input-group-text bg-dark text-light"
                      id="basic-addon2"
                    >
                      <i className="fas fa-search"></i>
                    </button>
                  </div>
                </form>
                <div className="bg-dark bg-opacity-50 py-3">
                  <h2 class="card-title">{data.name}</h2>
                  <p class="card-text lead">{date.toDateString()}</p>
                  <hr></hr>
                  <i className="fas fa-cloud fa-4x"></i>
                  <h1 className="fw-bolder mb-5">33.03 &deg;C</h1>
                  <p className="fw-bolder mb-0">cloud</p>
                  <p className="lead"> 30.0 &deg;C | 33.01&deg;C</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Searchweather;
