import React, { useState } from 'react';
import './App.css';
const api = {
  key: "a0df74752ee6fa22975dd7164bb0725a",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weathers, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }
  let  bgimage = `https://source.unsplash.com/1300x800/?`;
  let date = new Date();
  return (
    <div className="app" style={{backgroundImage:`url(${bgimage})`}}>
      <main >
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search here by City Name...."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        <div className="row justify-content-center">
          <div className="col-md-4">
            {typeof weathers.main != "undefined" ? (
              <div className="card text-white text-center border-0">
                <img
                  src={`https://source.unsplash.com/600x900/?${weathers.weather[0].description}`}
                  className="card-img"
                  alt="..."
                />
                <div className="card-img-overlay">

                  <div className="bg-dark bg-opacity-50 py-3">
                    <h2 className="card-title">
                      {weathers.name},{weathers.sys.country}
                    </h2>
                    <p className="card-text lead">{date.toDateString()}</p>
                    <hr></hr>
                    <div>
                      <p className="lead"> Max <span className="px-2">{Math.round(weathers.main.temp_max)}&deg;C</span>|  Min <span className="px-2"> {Math.round(weathers.main.temp_min)}&deg;C</span></p>
                      <i className="fas fa-cloud fa-4x"></i>
                      <h1 className="fw-bolder mb-4">
                        {Math.round(weathers.main.temp)}&deg;C
                      </h1>
                    </div>
                    <h3>{weathers.weather[0].main}</h3>
                    <h5> Description : {weathers.weather[0].description};</h5>
                    <h4 className="my-3 mb-1">Humidity  {Math.round(weathers.main.humidity)} | Pressure {Math.round(weathers.main.pressure)}</h4>
                    <h3>Wind</h3>
                    <p>Deg : {weathers.wind.deg} | Speed: {Math.round(weathers.wind.speed)} | Gust: {Math.round(weathers.wind.gust)}</p>
                  </div>

                </div>
              </div>
            ) : (" ")}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
