import React, { useState } from 'react';
import './App.css';
const api = {
  key: "a0df74752ee6fa22975dd7164bb0725a",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});
 const [image, setImage] = useState({});
  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
    if (evt.key === "Enter") {
      fetch('https://api.unsplash.com/photos/?client_id=yqIbwZLj9SbTwftC9vtqExmWzP36C2QlrG3kxBjYnEc')
        .then(res => res.json())
        .then(results => {
          setImage(results);
          console.log(results);
        });
    }
  }


  let date = new Date();
  return (
    <div>
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search here by City Name...."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        <div className="row justify-content-center">
          <div className="col-md-4">
          {typeof weather.main != "undefined" ? (
            <div className="card text-white text-center border-0">
              <img
                src={image.urls}
                className="card-img"
                alt="..."
              />
              <div className="card-img-overlay">

                  <div className="bg-dark bg-opacity-50 py-3">
                    <h2 className="card-title">
                      {weather.name},{weather.sys.country}
                    </h2>
                    <p className="card-text lead">{date.toDateString()}</p>
                    <hr></hr>
                    <i className="fas fa-cloud fa-4x"></i>
                    <h1 className="fw-bolder mb-5">
                      {Math.round(weather.main.temp)}&deg;C
                    </h1>
                    <p className="fw-bolder mb-0">cloud</p>
                    <p className="lead"> 30.0 &deg;C | 33.01&deg;C</p>
                  </div>

              </div>
            </div>
            ) : (" ")}
          </div> 
        </div>
      </main>
    </div>
  );
}

export default App;
