import React, { useState } from 'react';
import './App.css';
const api = {
  key: "a0df74752ee6fa22975dd7164bb0725a",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }



  return (
    <div className={(typeof weather.main != "undefined") ? ((weather.main.temp > 16) ? 'app warm' : 'app') : 'app'}>
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {(typeof weather.main != "undefined") ? (
           <div className="bg-dark bg-opacity-50 py-3">
           <h2 className="card-title">
             {weather.name},{weather.sys.country}
           </h2>
           <p className="card-text lead">{date.toDateString()}</p>
           <hr></hr>
           <i className="fas fa-cloud fa-4x"></i>
           <h1 className="fw-bolder mb-5">
             {Math.round(weather.main.temp)}&deg;C
           </h1>
           <p className="fw-bolder mb-0">cloud</p>
           <p className="lead"> 30.0 &deg;C | 33.01&deg;C</p>
         </div>
        ) : ('')}
      </main>
    </div>
  );
}

export default App;
