import React, { useState} from "react";

function Searchweather() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState({});
  const apikey = "a0df74752ee6fa22975dd7164bb0725a";

  const search = (evt) => {
    if (evt.key === "Enter") {
      fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&APPID=${apikey}`
      )
        .then((res) => res.json())
        .then((result) => {
          setWeather(result);
          setCity("");
          console.log(result);
        });
    }
  };

  let date = new Date();
  return (
    <div>
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-4">
            <div className="card text-white text-center border-0">
              <img
                src="https://source.unsplash.com/600x900/?nature,water"
                className="card-img"
                alt="..."
              />
              <div className="card-img-overlay">
                <form>
                  <div className="input-group mb-4 w-75 mx-auto">
                    <input
                      type="search"
                      className="form-control"
                      placeholder="Location..."
                      aria-label="Location..."
                      aria-describedby="basic-addon2"
                      onChange={e => setCity(e.target.value)}
                      value={city}
                      on
                    />
                  </div>
                </form>
                { (typeof weather.main != "undefined") ? (
                  <div className="bg-dark bg-opacity-50 py-3">
                     <h2 className="card-title">{weather.name},{weather.sys.country}</h2>
                     <p className="card-text lead">{date.toDateString()}</p>
                     <hr></hr>
                     <i className="fas fa-cloud fa-4x"></i>
                     <h1 className="fw-bolder mb-5">{ Math.round( weather.main.temp)}&deg;C</h1>
                     <p className="fw-bolder mb-0">cloud</p>
                     <p className="lead"> 30.0 &deg;C | 33.01&deg;C</p>
                   </div>
                ):(' ')}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Searchweather;
