import React, { useState } from 'react';
import './App.css';
const api = {
  key: "a0df74752ee6fa22975dd7164bb0725a",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weathers, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }


  let date = new Date();
  return (
    <div>
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search here by City Name...."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        <div className="row justify-content-center">
          <div className="col-md-4">
          {typeof weathers.main != "undefined" ? (
            <div className="card text-white text-center border-0">
              <img
                src="https://source.unsplash.com/600x900/?nature,water"
                className="card-img"
                alt="..."
              />
              <div className="card-img-overlay">

                  <div className="bg-dark bg-opacity-50 py-3">
                    <h2 className="card-title">
                      {weathers.name},{weathers.sys.country}
                    </h2>
                    <p className="card-text lead">{date.toDateString()}</p>
                    <hr></hr>
                    <i className="fas fa-cloud fa-4x"></i>
                    <h1 className="fw-bolder mb-5">
                      {Math.round(weathers.main.temp)}&deg;C
                    </h1>
                    <p className="fw-bolder mb-0">cloud</p>
                    <p className="lead"> Max {Math.round(weathers.main.temp_max)}&deg;C |  Min {Math.round(weathers.main.temp_min)}&deg;C</p>
                    <h4>Humidity  {Math.round(weathers.main.humidity)} | Pressure {Math.round(weathers.main.pressure)}</h4>
                    <h3>Description {weathers.weather.description}</h3>
                    <p> Wind Deg : {weathers.wind.deg} | Wind Speed: {Math.round(weathers.wind.speed) }</p>
                  </div>

              </div>
            </div>
            ) : (" ")}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
